mapper.select:
select c.id, c.fleetId, c.plateNo, c.carType,c.companyId, c.vehicleOwner, c.tel, c.buyDatetime,c.driverId,c.statues,co.company,f.fleetName,ct.modelName,d.driverName from t_archives_car c LEFT JOIN t_archives_company co ON c.companyId = co.id LEFT JOIN t_system_fleet f ON c.fleetId = f.id LEFT JOIN t_archives_car_type ct ON c.carType = ct.id LEFT JOIN t_archives_driver d ON c.driverId = d.id where 1=1
and c.fleetId = #{fleetId}
and c.plateNo like '%${plateNo}%'

mapper.update:
update t_archives_car set fleetId=#{fleetId},plateNo=#{plateNo},carType=#{carType}
,companyId=#{companyId},vehicleOwner=#{vehicleOwner},tel=#{tel}, buyDatetime=#{buyDatetime}, driverId=#{driverId}, statues=#{statues}
where id=#{id}
