mapper.select:
select u.*,e.name as empName,f.fleetName,c.company,r.roleName from t_system_user u left join t_system_employee e on u.empId = e.id LEFT JOIN t_system_fleet f ON u.fleetId = f.id LEFT JOIN t_archives_company c ON c.id = u.companyId LEFT JOIN t_system_role r ON u.roleId = r.id WHERE u.loginName!='root'
and u.loginName like '%${userName}%'
and u.fleetId = #{fleetId}
and u.fleetId =#{queryfleetId}
order by u.crTime desc

select u.*,e.name as empName,f.fleetName,c.company,r.roleName from t_system_user u left join t_system_employee e on u.empId = e.id LEFT JOIN t_system_fleet f ON u.fleetId = f.id LEFT JOIN t_archives_company c ON c.id = u.companyId LEFT JOIN t_system_role r ON u.roleId = r.id WHERE u.loginName!='root' AND u.fatherId = #{userId}
and u.loginName like '%${userName}%'
and u.fleetId = #{fleetId}
order by u.crTime desc

select * from  t_system_user
where 1=1

	and fleetId = #{fleetId}

mapper.update:
update  t_system_user set loginName=#{loginName},empId=#{empId},isAdmin=#{isAdmin},flag=#{flag}
,email=#{email},remark=#{remark}, modifier=#{modifier}, moTime=#{moTime} ,fleetId=#{fleetId},password=#{password},fatherId=#{fatherId},roleId=#{roleId}
where id=#{id}

update  t_system_user set 
password=#{password}
where empId=#{id}

mapper.select:
SELECT u.*,e.`name` FROM t_system_user  u
LEFT JOIN t_system_employee e ON u.empId = e.id
WHERE u.id =#{currUserId}
