mapper.select:
select * from  t_system_user
where 1=1

	and fleetId = #{fleetId}

mapper.update:
update  t_system_user set loginName=#{loginName},empId=#{empId},isAdmin=#{isAdmin},flag=#{flag}
,email=#{email},remark=#{remark}, modifier=#{modifier}, moTime=#{moTime} ,fleetId=#{fleetId},password=#{password}
where id=#{id}

update  t_system_user set 
password=#{password}
where empId=#{id}
