mapper.select:
SELECT a.*, f.fleetName,u.passengerName,c.company,d.driverName ,ct.modelName,cc.plateNo FROM t_car_apply a LEFT JOIN t_system_fleet f ON a.fleetId = f.id LEFT JOIN t_archives_passenger u ON a.userId = u.id LEFT JOIN t_archives_company c ON a.companyId = c.id LEFT JOIN t_archives_driver d ON a.driverId = d.id LEFT JOIN t_archives_car_type ct ON a.carTypeId = ct.id LEFT JOIN t_archives_car cc ON a.plateNoId = cc.id WHERE a.statuesId = #{statuesId}
AND a.fleetId =#{fleetId}
AND a.carApplyNo like '%${carApplyNo}%'

SELECT a.*, f.fleetName,u.passengerName,c.company,d.driverName ,ct.modelName,cc.plateNo FROM t_car_apply a LEFT JOIN t_system_fleet f ON a.fleetId = f.id LEFT JOIN t_archives_passenger u ON a.userId = u.id LEFT JOIN t_archives_company c ON a.companyId = c.id LEFT JOIN t_archives_driver d ON a.driverId = d.id LEFT JOIN t_archives_car_type ct ON a.carTypeId = ct.id LEFT JOIN t_archives_car cc ON a.plateNoId = cc.id WHERE 1=1
AND a.fleetId =#{fleetId}
AND a.carApplyNo like '%${carApplyNo}%'

SELECT a.*, f.fleetName,u.passengerName,c.company,d.driverName ,ct.modelName FROM t_car_apply a LEFT JOIN t_system_fleet f ON a.fleetId = f.id LEFT JOIN t_archives_passenger u ON a.userId = u.id LEFT JOIN t_archives_company c ON a.companyId = c.id LEFT JOIN t_archives_driver d ON a.driverId = d.id LEFT JOIN t_archives_car_type ct ON a.carTypeId = ct.id WHERE a.statuesId = '20'
AND a.fleetId =#{fleetId}
AND a.carApplyNo like '%${insanitycarApplyNo}%'

SELECT a.*, f.fleetName,u.passengerName,c.company,d.driverName ,ct.modelName FROM t_car_apply a LEFT JOIN t_system_fleet f ON a.fleetId = f.id LEFT JOIN t_archives_passenger u ON a.userId = u.id LEFT JOIN t_archives_company c ON a.companyId = c.id LEFT JOIN t_archives_driver d ON a.driverId = d.id LEFT JOIN t_archives_car_type ct ON a.carTypeId = ct.id WHERE a.statuesId = '30'
AND a.fleetId =#{fleetId}
AND a.carApplyNo like '%${carApplyNo}%'

SELECT l.*,u.loginName ,f.fleetName FROM  t_car_approve_log l
LEFT JOIN t_system_user u  ON l.userId = u.id
LEFT JOIN t_system_fleet f ON l.fleetId = f.id
WHERE  1=1

    AND l.fleetId =#{fleetId}


    AND   l.userId =#{userId}

SELECT dl.* ,f.fleetName,c.plateNo,d.driverName,u.loginName FROM t_car_dispatch_log dl LEFT JOIN t_system_fleet f ON dl.fleetId = f.id LEFT JOIN t_archives_car c ON dl.plateNoId = c.id LEFT JOIN t_archives_driver d ON dl.driverId = d.id LEFT JOIN t_system_user u ON dl.userId = u.id WHERE 1=1
AND dl.fleetId =#{fleetId}
AND dl.userId =#{userId}

mapper.update:
UPDATE t_car_apply
SET statuesId = '20'
WHERE carApplyNo=#{carApplyNo}

UPDATE t_car_apply
SET statuesId = '0'
WHERE carApplyNo=#{carApplyNo}

UPDATE t_car_apply
SET statuesId = '30',driverId=#{driverId},plateNoId=#{plateNoId},carTypeId=#{carTypeId}
WHERE carApplyNo=#{carApplyNo}
